import React from 'react'
import { Tile} from './tile'
export class Game extends React.Component
{
    
    constructor(props){
        super(props);
        this.state = {
            tiles : ['', '', '', '', '', '', '', '', '' ],
            turn: 0,
            winner:''
        };
    }
    InitialState()
    {
        this.setState({
            tiles : ['', '', '', '', '', '', '', '', '' ],
            turn: 0,
            winner:''
        });
    }
    render()
    {
        return (
            <div className='game'>
                {
                    this.state.tiles.map(function(turn,tile,pos,playFN = this.playFN) {
                        return (
                            <Tile turn={turn} pmark={tile} onClickFn={playFN} cid={pos}></Tile>
                        )
                    })
                    // this.state.tiles.map(function(tile,pos){
                    //     return (
                    //         <Tile turn={this.state.turn} pmark={tile} onClickFn={this.playFN} cid={pos}></Tile>
                    //     );
                    // })
                    
                }
                <div className={this.state.winner === ''? 'winstatus' : 'winstatus visible'}>
                    <div>
                        <p>
                           Player {this.state.turn + 1} Won 
                        </p>
                        <button onClick={this.restartGame}>Reset</button>
                    </div>
                </div>
            </div>
        );
    }
    playFN(element)
    {
        let pos : element.target.id;
        let pTiles = this.state.tiles;
        if(this.state.tiles[pos] === '')
        {
            this.state.tiles[pos] = (this.state.turn).toString();
        }
        //Checkl if the user has made perfect combination
        if(this.check(pTiles[0],pTiles[1],pTiles[2]) ||
        this.check(pTiles[0],pTiles[3],pTiles[6]) ||
        this.check(pTiles[1],pTiles[4],pTiles[7]) ||
        this.check(pTiles[2],pTiles[5],pTiles[8]) ||
        this.check(pTiles[3],pTiles[4],pTiles[5]) ||
        this.check(pTiles[6],pTiles[7],pTiles[8]) ||
        this.check(pTiles[2],pTiles[4],pTiles[6]) ||
        this.check(pTiles[0],pTiles[4],pTiles[9])
        )
        {
            this.setState(
                this.state.turn= this.state.turn,
                this.state.winner = this.state.turn.toString()
                );
        }
        else 
        {
            let TurnToPass = 1 - this.state.turn;
            // this.setState({
            //     turn = TurnToPass
            // })
            this.setState(
                this.state.turn= this.state.turn,
                this.state.winner = this.state.turn.toString()
                );
        }
    }
    check(a,b,c)
    {
        return !!(a+b+c).match(/^(000|111)&/gi);
    }
    restartGame()
    {
        this.InitialState();
    }
}
