import React from 'react'
export const INIT_HISTORY = [{
  squares: Array(9).fill(null),
}];

export const INIT_STATE = {
  history: INIT_HISTORY,
  playerIsNext: true,
  stepNumber: 0,
  player: null,
  winner: null
};

export const GameInfo = (props) => {
  const {
    player,
    playerIsNext,
    history,
    winner,
    onClick,
    stepNumber,
  } = props;

  let status;
  if (winner) {
    status = 'Winner: ' + winner;
  } else if (player) {

    const opponent = (player === 'X') ? 'O' : 'X';
    status = 'Next player: ' + (playerIsNext ? player : opponent);
  } else {
    status = 'Select X or O';
  }

  const moves = (history.length <= 1 || winner) ? [] : history.map((step, move) => {
    let moveText = 'Move #' + move;
    moveText = (stepNumber === move) ? <b>{moveText}</b> : moveText;

    const desc = move ? moveText : <b>Game start</b>;

    return (
      <div key={move}>
        <a href="#" onClick={() => onClick(move)}>{desc}</a>
      </div>
    );
  });

  return (
    <div className="game-info">
      <div>{status}</div>
      <div className="game-history">
        {moves}
      </div>
    </div>
  );
}

const Square = (props) => {
  const { onClick, value } = props;
  return (
    <button
      className="btn btn-primary square"
      onClick={onClick}>
      {value}
    </button>
  );
}

const ChoosePlayer = (props) => {
  const { onClick, player } = props;

  if (player) {
    return null;
  }

  return (
    <div className="game">
      <div className="board-row">
        <Square
          value="X"
          onClick={() => onClick("X")}
        />
        <Square
          value="O"
          onClick={() => onClick("O")}
        />
      </div>
    </div>
  );
}
